INSERT INTO `zhihu_question` VALUES (12, 522472424, '汽车,电动汽车,油价,新能源汽车,汽车选购', 'https://www.zhihu.com/question/522472424', '油价又涨了，到底买油车还是新能源汽车？', '如今的汽车行业风云变幻，受油价飙升，碳中和等大环境影响，似乎在鼓励人们选购纯电车，作为普通消费者，在当今的局势下，到底该选购一辆什么样的车才是合适的？', '2022-04-21 13:07:52', '2022-04-21 13:07:52', 405, 0, 618453, 0, '2022-04-21 13:07:52', '2022-04-21 13:07:52');
